/**
 * Enumerates the different HUD elements that can be manipulated.
 * 
 * 枚举可以操控的不同 HUD 元素。
 */
export enum HudElement {
    PaperDoll = 0, // 纸娃娃
    Armor = 1, // 护甲
    ToolTips = 2, // 工具提示
    TouchControls = 3, // 触摸控制
    Crosshair = 4, // 十字准星
    Hotbar = 5, // 快捷栏
    Health = 6, // 生命值
    ProgressBar = 7, // 进度条
    Hunger = 8, // 饥饿值
    AirBubbles = 9, // 气泡
    HorseHealth = 10, // 马的生命值
    StatusEffects = 11, // 状态效果
    ItemText = 12, // 物品文字
}