/**
 * Enum describing a structure's placement rotation.
 * 
 * 描述结构放置旋转的枚举。
 */
export enum StructureRotation {
    /**
     * @remarks
     * No rotation.
     * 
     * 没有旋转。
     */
    None = 'None',
    /**
     * @remarks
     * 180 degree rotation.
     * 
     * 180度旋转。
     */
    Rotate180 = 'Rotate180',
    /**
     * @remarks
     * 270 degree rotation.
     * 
     * 270度旋转。
     */
    Rotate270 = 'Rotate270',
    /**
     * @remarks
     * 90 degree rotation.
     * 
     * 90度旋转。
     */
    Rotate90 = 'Rotate90',
}