/**
 * Represents a two-directional vector.
 * 
 * 表示一个二维向量。
 */
export interface Vector2 {
    /**
     * @remarks
     * X component of the two-dimensional vector.
     * 
     * 二维向量的 X 分量。
     */
    x: number;
    /**
     * @remarks
     * Y component of the two-dimensional vector.
     * 
     * 二维向量的 Y 分量。
     */
    y: number;
}