/**
 * Contains a description of a vector.
 * 
 * 包含向量描述。
 */
export interface Vector3 {
    /**
     * @remarks
     * X component of this vector.
     * 
     * 向量的 X 分量。
     */
    x: number;
    /**
     * @remarks
     * Y component of this vector.
     * 
     * 向量的 Y 分量。
     */
    y: number;
    /**
     * @remarks
     * Z component of this vector.
     * 
     * 向量的 Z 分量。
     */
    z: number;
}